const contact = $input.first().json;

// Determine channel based on segment preferences
const channelMap = {
  're_investors': 'email',
  'house_buyers': 'sms',
  'bird_doggers': 'email',
  'jv_partners': 'email',
  'coaching_students': 'email',
  'wholesalers': 'email',
  'lenders': 'email',
  'sellers': 'sms',
  'general_leads': 'email'
};

// Determine message type based on status
const messageTypeMap = {
  'hot_lead': 'update_hot',
  'active_investor': 'update_active',
  'passive_investor': 'update_passive',
  'jv_potential': 'update_jv',
  'objection_holder': 'value_content',
  'dormant': 'reengagement',
  'tire_kicker': 'value_content'
};

const channel = contact.preferred_channel || channelMap[contact.segment] || 'email';
const messageType = messageTypeMap[contact.investor_status] || 'update_general';

// Calculate scheduled time (spread throughout the day)
const now = new Date();
const randomMinutes = Math.floor(Math.random() * 240); // Spread over 4 hours
const scheduledFor = new Date(now.getTime() + randomMinutes * 60000);

return {
  json: {
    contact_id: contact.id,
    ghl_contact_id: contact.ghl_id,
    first_name: contact.first_name,
    email: contact.email,
    phone: contact.phone,
    segment: contact.segment,
    investor_status: contact.investor_status,
    latest_notes: contact.latest_notes,
    channel: channel,
    message_type: messageType,
    scheduled_for: scheduledFor.toISOString()
  }
};

const queueEntry = $input.first().json;

return {
  json: {
    event_type: "touchpoint_scheduled",
    event_category: "system1_scheduler",
    contact_id: queueEntry.ghl_contact_id,
    success: true,
    event_data: {
      queue_id: queueEntry.id,
      channel: queueEntry.channel,
      message_type: queueEntry.message_type,
      scheduled_for: queueEntry.scheduled_for,
      contact_sync_id: queueEntry.contact_sync_id
    }
  }
};
